import org.junit.Test;

import java.util.ArrayList;
import java.util.List;
import java.util.Random;

public class Tests {

    Random random = new Random(101);

    @Test
    public void test() throws InterruptedException {
        String s = "";
        for (int i = 0; i <= 1000000000; i++) {
            List<Mutation> mutations = new ArrayList<>();
            for (int j = 0; j <= 30; j++) {
                mutations.add(new Mutation(s.length()));
            }
            RopeProblem.RopeNaive naive = new RopeProblem.RopeNaive(s);
            RopeProblem.Rope rope = new RopeProblem.Rope(s);
            if(i % 100000 == 0){
                System.out.println(i);
            }
            for (Mutation m : mutations) {
                rope.process(m.i + 1, m.j + 1, m.k);
                naive.process(m.i, m.j, m.k);
                if (!rope.result().equals(naive.result())) {
                    System.out.println(m.i + " " + m.j + " " + m.k + "-" + rope.result());
                    throw new RuntimeException("Not equals: " + rope.result() + " : " + naive.result());
                }
            }
        }

    }

    class Mutation {
        int i, j, k;

        public Mutation(int s) {
            i = random.nextInt(s);
            j = i + random.nextInt(s - i);

            int bound = s - (j - i + 1);
            if (bound > 0) {
                k = random.nextInt(bound);
            } else if (bound == 0) {
                k = 0;
            } else {
                throw new IllegalStateException(i + " " + j + " " + s);
            }

        }
    }
}
